    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        //con archivos
        int val = 0;
        try {
            File archivo = new File("Usuario2.txt");

            archivo.delete();
            if (archivo.exists()) {
                archivo.delete();
            }
            if (!archivo.exists()) {  //ve si el archivo existe
                archivo.createNewFile();   //si no existe lo crea
            }
            FileWriter fw = new FileWriter(archivo, true);  //true-> continua escribiendo ; false -> sobre escribe los datos del archivo
            
            BufferedWriter bu = new BufferedWriter(fw);
            //validando nombre
            String nom = txtNombre.getText();
            Pattern pa = Pattern.compile("^[a-zA-Z\\s\\xE1\\xE9\\xED\\xF3\\xFA\\xC1\\xC9\\xCD\\xD3\\xDA]{3,}$");
            Matcher m = pa.matcher(nom);
            boolean coincidencia = m.find();

            if (coincidencia) {
                bu.write(txtNombre.getText()); //escribimos
                bu.write("/");
                val++;
            } else {
                JOptionPane.showMessageDialog(null, "Escriba nombre nuevamente");
            }
            //validando telefono
            String tele = txtTelefono.getText();
            Pattern patT = Pattern.compile("([0-9]{9})");
            Matcher maT = patT.matcher(tele);
            boolean coincidenciaT = maT.find();
            if (coincidenciaT) {
                bu.write(txtTelefono.getText()); //escribimos
                bu.write("/");
                val++;
            }
            //validando email
            String correo = txtEmail.getText();
            Pattern pat = Pattern.compile("[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?");
            Matcher ma = pat.matcher(correo);

            boolean coincidenciaa = ma.find();

            if (coincidenciaa) {
                bu.write(txtEmail.getText());  //escibimos
                bu.write("/");
                val++;
            } else {
                JOptionPane.showMessageDialog(null, "Correo inválido, intentelo nuevamente");
            }
            String Region = (String) ComboBoxRegion.getSelectedItem();
            String Comuna = (String) ComboBoxComuna.getSelectedItem();
            bu.write(Region);  //escibimos   
            bu.write("/");
            bu.write(Comuna);
            bu.flush();
            bu.close();  //cerramos el fichero
            JOptionPane.showMessageDialog(null, "Ingresado Correctamente");   //si los datos se ingresan correctamente mostrara el mensaje
        } catch (IOException ex) {
            JOptionPane.showMessageDialog(null, "ERROR: " + ex);
        }   //excepcion del try catch

        //Cambio de JFrame
        if (val == 3) {
            simulador2 s = new simulador2();
            s.setVisible(true);
            this.dispose();
        } else {
            JOptionPane.showMessageDialog(null, "Debe completar todos los campos");
        }

        //
        //Cambio de JFrame
        simulador3 sim = new simulador3();
        sim.setVisible(true);
        this.dispose();


    }//GEN-LAST:event_jButton1ActionPerformed

